mapper.select:
select m.* from (select rownum rowno, x.* from ( select z.* from (select sum(a.jgcs) jgcs, sum(a.fmcs) fmcs, sum(a.tdcs) tdcs, sum(a.zjcs) zjcs, sum(a.qtfmcs) qtfmcs, sum(a.jgcs+a.fmcs+a.tdcs+a.zjcs+a.qtfmcs) xiaoji , ${selectConditon}
from t_qycftj a where 1=1
group by ${groupBy}
) z order by ${pageSort} ) x ) m where rowno >${beginRow} and rowno <= ${endRow}

	select count(1) from (
select count(1)  from t_qycftj a
where 1=1


		group by ${groupBy}

)


	SELECT * FROM (
	 SELECT A.*, ROWNUM rowno
	 from ( 
	   SELECT PCS.DEPARTBREVITYCODE gxdwdm,
		      PCS.DEPARTNAME gxdwmc,
		       NVL(JGCS.JGCS,'0')+ NVL(FMCS.FMCS,'0')+ NVL(TDCS.TDCS,'0')+NVL(ZJCS.ZJCS,'0')+ NVL(QTCS.QTCS,'0') xiaoji,
		       NVL(JGCS.JGCS,'0') jgcs,
		       NVL(FMCS.FMCS,'0') fmcs,
		       NVL(TDCS.TDCS,'0') tdcs,
		       NVL(ZJCS.ZJCS,'0') zjcs,
		       NVL(QTCS.QTCS,'0') qtfmcs,
		       '' csbm,
		       '' csmc

	   from (SELECT DEPT.DEPARTCODE, DEPT.DEPARTBREVITYCODE, DEPT.DEPARTNAME, DEPT.NODEORDER
		          FROM T_DEPARTMENT DEPT
		         WHERE DEPT.DEPARTLEVEL = #{gxdwlevel}
			           AND DEPT.DEPARTCODE LIKE #{gxdwbm}  order by dept.nodeorder ) PCS,

			(SELECT SUBSTR(qycf.ssdwbm, 0, LENGTH(DEPT.DEPARTBREVITYCODE)) ssdwbm,
		               COUNT(1) JGCS
		          FROM t_qycf qycf, T_DEPARTMENT DEPT
		         WHERE DEPT.DEPARTLEVEL = #{gxdwlevel}
			       AND DEPT.DEPARTCODE LIKE #{gxdwbm}
		           AND qycf.ssdwbm LIKE DEPT.DEPARTBREVITYCODE || '%'
		           AND qycf.djsj BETWEEN #{qssj,jdbcType=TIMESTAMP} AND #{jzsj,jdbcType=TIMESTAMP}
		           AND qycf.cfjgdm ='1'

		         GROUP BY SUBSTR(qycf.ssdwbm, 0, LENGTH(DEPT.DEPARTBREVITYCODE))) JGCS,

		    (SELECT SUBSTR(qycf.ssdwbm, 0, LENGTH(DEPT.DEPARTBREVITYCODE)) ssdwbm,
		               COUNT(1) FMCS
		          FROM t_qycf qycf, T_DEPARTMENT DEPT
		         WHERE DEPT.DEPARTLEVEL = #{gxdwlevel}
			       AND DEPT.DEPARTCODE LIKE #{gxdwbm}
		           AND qycf.ssdwbm LIKE DEPT.DEPARTBREVITYCODE || '%'
		           AND qycf.djsj BETWEEN #{qssj,jdbcType=TIMESTAMP} AND #{jzsj,jdbcType=TIMESTAMP}
		           AND (qycf.cfjgdm ='2' or qycf.cfjgdm ='3' or qycf.cfjgdm ='4')

		         GROUP BY SUBSTR(qycf.ssdwbm, 0, LENGTH(DEPT.DEPARTBREVITYCODE))) FMCS,

		    (SELECT SUBSTR(qycf.ssdwbm, 0, LENGTH(DEPT.DEPARTBREVITYCODE)) ssdwbm,
		               COUNT(1) TDCS
		          FROM t_qycf qycf, T_DEPARTMENT DEPT
		         WHERE DEPT.DEPARTLEVEL = #{gxdwlevel}
			       AND DEPT.DEPARTCODE LIKE #{gxdwbm}
		           AND qycf.ssdwbm LIKE DEPT.DEPARTBREVITYCODE || '%'
		           AND qycf.djsj BETWEEN #{qssj,jdbcType=TIMESTAMP} AND #{jzsj,jdbcType=TIMESTAMP}
		           AND (qycf.cfjgdm ='5' or qycf.cfjgdm ='6')

		         GROUP BY SUBSTR(qycf.ssdwbm, 0, LENGTH(DEPT.DEPARTBREVITYCODE))) TDCS,

		    (SELECT SUBSTR(qycf.ssdwbm, 0, LENGTH(DEPT.DEPARTBREVITYCODE)) ssdwbm,
		               COUNT(1) ZJCS
		          FROM t_qycf qycf, T_DEPARTMENT DEPT
		         WHERE DEPT.DEPARTLEVEL = #{gxdwlevel}
			       AND DEPT.DEPARTCODE LIKE #{gxdwbm}
		           AND qycf.ssdwbm LIKE DEPT.DEPARTBREVITYCODE || '%'
		           AND qycf.djsj BETWEEN #{qssj,jdbcType=TIMESTAMP} AND #{jzsj,jdbcType=TIMESTAMP}
		           AND qycf.cfjgdm ='7' 

		         GROUP BY SUBSTR(qycf.ssdwbm, 0, LENGTH(DEPT.DEPARTBREVITYCODE))) ZJCS,

		    (SELECT SUBSTR(qycf.ssdwbm, 0, LENGTH(DEPT.DEPARTBREVITYCODE)) ssdwbm,
		               COUNT(1) QTCS
		          FROM t_qycf qycf, T_DEPARTMENT DEPT
		         WHERE DEPT.DEPARTLEVEL = #{gxdwlevel}
			       AND DEPT.DEPARTCODE LIKE #{gxdwbm}
		           AND qycf.ssdwbm LIKE DEPT.DEPARTBREVITYCODE || '%'
		           AND qycf.djsj BETWEEN #{qssj,jdbcType=TIMESTAMP} AND #{jzsj,jdbcType=TIMESTAMP}
		           AND qycf.cfjgdm ='9' 

		         GROUP BY SUBSTR(qycf.ssdwbm, 0, LENGTH(DEPT.DEPARTBREVITYCODE))) QTCS

		   WHERE PCS.DEPARTBREVITYCODE = JGCS.ssdwbm(+)
		   AND PCS.DEPARTBREVITYCODE = FMCS.ssdwbm(+)
		   AND PCS.DEPARTBREVITYCODE = TDCS.ssdwbm(+)
		   AND PCS.DEPARTBREVITYCODE = ZJCS.ssdwbm(+)
		   AND PCS.DEPARTBREVITYCODE = QTCS.ssdwbm(+)
  		   ORDER BY ${pageSort}

	 ) A

  		WHERE ROWNUM <= #{endRow}
		)
		WHERE rowno > #{beginRow}

select x.* from ( select rownum rowno,z.* from (select NVL(sum(b.jgcsT+b.fmcsT+b.tdcsT+b.zjcsT+b.qtfmcsT+b.tbbycsT+b.wzjlcsT+b.qtjlcsT),'0') as jczsT, NVL(sum(b.jgcsT+b.fmcsT+b.tdcsT+b.zjcsT+b.qtfmcsT),'0') as cfzsT, NVL(sum(b.tbbycsT+b.wzjlcsT+b.qtjlcsT),'0') as jlzsT, NVL(sum(b.jgcs+b.fmcs+b.tdcs+b.zjcs+b.qtfmcs+b.tbbycs+b.wzjlcs+b.qtjlcs),'0') as jczs, NVL(sum(b.jgcs+b.fmcs+b.tdcs+b.zjcs+b.qtfmcs),'0') as cfzs, NVL(sum(b.tbbycs+b.wzjlcs+b.qtjlcs),'0') as jlzs, NVL(sum(b.jgcsT),'0') as jgcsT, NVL(sum(b.fmcsT),'0') as fmcsT, NVL(sum(b.tdcsT),'0') as tdcsT, NVL(sum(b.zjcsT),'0') as zjcsT, NVL(sum(b.qtfmcsT),'0') as qtfmcsT, NVL(sum(b.tbbycsT),'0') as tbbycsT, NVL(sum(b.wzjlcsT),'0') as wzjlcsT, NVL(sum(b.qtjlcsT),'0') as qtjlcsT, NVL(sum(b.jgcs),'0') as jgcs, NVL(sum(b.fmcs),'0') as fmcs, NVL(sum(b.tdcs),'0') as tdcs, NVL(sum(b.zjcs),'0') as zjcs, NVL(sum(b.qtfmcs),'0') as qtfmcs, NVL(sum(b.tbbycs),'0') as tbbycs, NVL(sum(b.wzjlcs),'0') as wzjlcs, NVL(sum(b.qtjlcs),'0') as qtjlcs , ${selectConditon}
from (select sum(case when a.tjrq < #jzrq:DATE#+1 then a.jgcs end) as jgcsT, sum(case when a.tjrq < #jzrq:DATE#+1 then a.fmcs end) as fmcsT, sum(case when a.tjrq < #jzrq:DATE#+1 then a.tdcs end) as tdcsT, sum(case when a.tjrq < #jzrq:DATE#+1 then a.zjcs end) as zjcsT, sum(case when a.tjrq < #jzrq:DATE#+1 then a.qtfmcs end) as qtfmcsT, sum(case when a.tjrq < #jzrq:DATE#+1 then a.tbbycs end) as tbbycsT, sum(case when a.tjrq < #jzrq:DATE#+1 then a.wzjlcs end) as wzjlcsT, sum(case when a.tjrq < #jzrq:DATE#+1 then a.qtjlcs end) as qtjlcsT, sum(case when a.tjrq >=#qsrq:DATE# and a.tjrq < #jzrq:DATE#+1 then a.jgcs end) as jgcs, sum(case when a.tjrq >=#qsrq:DATE# and a.tjrq < #jzrq:DATE#+1 then a.fmcs end) as fmcs, sum(case when a.tjrq >=#qsrq:DATE# and a.tjrq < #jzrq:DATE#+1 then a.tdcs end) as tdcs, sum(case when a.tjrq >=#qsrq:DATE# and a.tjrq < #jzrq:DATE#+1 then a.zjcs end) as zjcs, sum(case when a.tjrq >=#qsrq:DATE# and a.tjrq < #jzrq:DATE#+1 then a.qtfmcs end) as qtfmcs, sum(case when a.tjrq >=#qsrq:DATE# and a.tjrq < #jzrq:DATE#+1 then a.tbbycs end) as tbbycs, sum(case when a.tjrq >=#qsrq:DATE# and a.tjrq < #jzrq:DATE#+1 then a.wzjlcs end) as wzjlcs, sum(case when a.tjrq >=#qsrq:DATE# and a.tjrq < #jzrq:DATE#+1 then a.qtjlcs end) as qtjlcs , ${selectConditon}
from t_qycftj a where 1=1
group by ${groupBy}
) b
group by ${groupBy}
order by ${pageSort} ) z) x where rowno >${beginRow} and rowno <= ${endRow}

	select count(1) from (
select count(1)  from t_qycftj a
where 1=1


		group by ${groupBy}

)
